library(shiny)

model <- lm(mpg ~ wt + hp, data = mtcars)

ui <- fluidPage(
  titlePanel("Simple Regression & Classification App"),
  
  sidebarLayout(
    sidebarPanel(
      numericInput("wt", "Car weight (1000 lbs):", value = 3, min = 1, max = 6, step = 0.1),
      numericInput("hp", "Horsepower:", value = 120, min = 50, max = 400, step = 10),
      actionButton("go", "Predict")
    ),
    
    mainPanel(
      h3("Results"),
      textOutput("predicted_mpg"),
      textOutput("classification")
    )
  )
)

server <- function(input, output) {
  
  prediction <- eventReactive(input$go, {
    newdata <- data.frame(wt = input$wt, hp = input$hp)
    predict(model, newdata)
  })
  
  output$predicted_mpg <- renderText({
    req(prediction())
    paste("Predicted mpg:", round(prediction(), 2))
  })
  
  output$classification <- renderText({
    req(prediction())
    if (prediction() > 20) {
      "Classification: Fuel efficient ðŸš—ðŸ’¨"
    } else {
      "Classification: Not fuel efficient ðŸ›»â›½"
    }
  })
}

shinyApp(ui = ui, server = server)
